import React, { Component } from "react";
import Jumbotron from "./components/Jumbotron";
import Nav from "./components/Nav";
import Input from "./components/Input";
import Button from "./components/Button";
import API from "./utils/API";
import { RecipeList, RecipeListItem } from "./components/RecipeList";
import { Container, Row, Col } from "./components/Grid";
import "./App.css";
// import { FacebookLoginButton } from 'react-social-login-buttons';
// import{ Form, FormGroup, Label } from 'reactstrap';


class App extends Component
{
  state = {
    recipes: [],
    recipeSearch: ""
  };

  handleInputChange = event => {
    // Destructure the name and value properties off of event.target
    // Update the appropriate state
    const { name, value } = event.target;
    this.setState({
      [name]: value
      //name input = value of input
      //[name]=recipeSearch
      //value= "chicken"
      //recipeSearch:"chicken"
    });

  };

  handleFormSubmit = event => {
    // When the form is submitted, prevent its default behavior, get recipes update the recipes state
    //App.js=> API.js=> apiRoute.js=>API.js=>App.js (returns out the array of object for chicken search query in json format)
    event.preventDefault();
    console.log("requesting handleFormSubmit "+this.state.recipeSearch);
    API.getRecipes(this.state.recipeSearch)
      .then(res => this.setState({ recipes: res.data }))
      .catch(err => console.log(err));

      console.log(this.state.recipes);
  };

  render() {
    return (
      <div>
        {/* <!-- Button trigger modal --> */}
<button type="button" className="btn btn-primary" data-toggle="modal" data-target="#exampleModal">
  About
</button>

{/* <!-- Modal --> */}
<div className="modal fade" id="exampleModal" tabindex="-1" role="dialog" aria-labelledby="exampleModalLabel" aria-hidden="true">
  <div className="modal-dialog" role="document">
    <div className="modal-content">
      <div classNameName="modal-header">
        <h5 className="modal-title" id="exampleModalLabel">About ReactEats</h5>
        <button type="button" className="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div className="modal-body">
        <p><strong>ReactEats</strong> is a food application created to find recipes from Recipe Puppy.</p>

        <p>The application began as a way to demonstrate the usage of React and usage of an API. The application was created via Atom, Adobe Photoshop, and Bootstrap.</p>
        <p><strong>ReactEats</strong> contains a series of recipes that anyone can search and view.</p>
          <p><strong>ReactEats</strong> is a React application which runs via Heroku deployment.</p>
      </div>
      <div className="modal-footer">
        <button type="button" className="btn btn-secondary" data-dismiss="modal">Close</button>
        {/* <button type="button" className="btn btn-primary">Save changes</button> */}
      </div>
    </div>
  </div>
</div>
        {/* <Form>
        <FormGroup>
          <Label>Email</Label>
          <Input type="email" placeholder="Email"/>
        </FormGroup>
        <FormGroup>
          <Label>Password</Label>
          <Input type="Password" placeholder="Password"/>
        </FormGroup>
        <Button className="btn-lg btn-dark btn-block">Login</Button>
        <div className="text-center pt-3">
          Or Continue with your social account
        </div>
        <FacebookLoginButton className="mt-3 mb-3"/>
        <div className="text-center">
          <a href="/signup">Sign Up</a>
          <span className="p-2"> | </span>
          <a href="/forgot-password">Forgot Password</a>

        </div>
      </Form> */}

        <Nav />
        <Jumbotron />
        <Container>
          <Row>
              <form>
            <Col size="md-12">
              <Container>


                  <Row>

                    <Col size="8 md-8 sm-8">
                      <Input
                        name="recipeSearch"
                        value={this.state.recipeSearch}
                        onChange={this.handleInputChange}
                        placeholder="Search For a Recipe"
                      />
                    </Col>
                    <Col size="4 md-4 sm-4">
                      <Button
                        onClick={this.handleFormSubmit}
                        type="success"
                        className="input-lg"
                      >
                        Search
                      </Button>
                    </Col>

                  </Row>


              </Container>
            </Col>
            </form>
          </Row>
          <Row>
            <Col size="xs-12">

              {!this.state.recipes.length ? (
                <h1 className="text-center">No Recipes to Display</h1>
              ) : (
                <ul className="list-group">
                  <li className="list-group-item list-group-item-light resultsTitle"><strong>Results</strong></li>
                <RecipeList>
                  {this.state.recipes.map(recipe => {
                    return (
                      <RecipeListItem
                        key={recipe.title}
                        title={recipe.title}
                        href={recipe.href}
                        ingredients={recipe.ingredients}
                        thumbnail={recipe.thumbnail}
                      />
                    );
                  })}
                </RecipeList>
              </ul>
              )}
            </Col>
          </Row>
        </Container>
      </div>
    );
  }
}

export default App;
